import React ,{ useEffect, useState} from "react";
import { useHistory, useParams } from "react-router-dom";
import { Link } from "react-router-dom";
export default function BookDetails({updateBook,handelRemoveBook, books}){
    const params = useParams()

    const book = books.find((book)=>book.id == params.id)
    const [like, setLike] = useState(book.liked)
    const history = useHistory()

    if(book===null){
        return <h1> Loading...</h1>
    }
    function handleLike(){
        const updatedBookLike ={
            id: book.id,
            title: book.title,
            image: book.image,
            liked: !book.liked,
            chapters: book.chapters,
            summary: book.summary

        }
        fetch(`http://localhost:3000/books/${book.id}`,{
            method: "PATCH",
            headers:{
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body:JSON.stringify({liked: !like})
        })
        .then (res => res.json())
        .then(()=>{
            updateBook(updatedBookLike)
            setLike(!like)
            })
    }
    function handleRemove(){
        fetch(`http://localhost:3000/books/${book.id}`,{
            method: 'DELETE',
            headers:{
              'Content-Type': 'application/json'
            }
          })
          .then(res => res.json())
          .then(()=>{
            history.push("/Books")
            handelRemoveBook(book)
        })


    }

    const likedStyles = {
        color: "red"
    }
    const dislikedStyles = {
        color: "black"
    }

    return (
        <div className="book-detail">
        <h1>{book.title}</h1>
            <img
            src={book.image}
            alt={book.title}
            className="book-detail-avatar"
            />
            {like? <h4 style={likedStyles} onClick={handleLike}>Like ♥</h4>:<h4 style={dislikedStyles} onClick={handleLike}>Like ♡</h4>}
            <h2>Total Chapters {book.chapters}</h2>
            <Link to={`/EditBook/${book.id}`}>Edit</Link>
            <br></br>
            <button onClick={handleRemove}>Remove</button>
            <h3>Summary</h3>
            <p> {book.summary} </p>
        </div>
    )
}
