import React, {useState} from "react";

import { Link } from "react-router-dom";

export default function BookCard({book,updateBook,handelRemoveBook}){
    const {id, title, image, chapters, liked, summary} = book
    const [like, setLike] = useState(liked)

    function handleLike(){
        const updatedBookLike ={
            id: id,
            title: title,
            image: image,
            liked: !liked,
            chapters: chapters,
            summary: summary

        }
        fetch(`http://localhost:9292/books/${id}`,{
            method: "PATCH",
            headers:{
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body:JSON.stringify({liked: !liked})
        })
        .then (res => res.json())
        .then(()=>{
            updateBook(updatedBookLike)
            setLike(!like)
            })
        console.log(like)
    }

    function handleRemove(){
        fetch(`http://localhost:3000/books/${id}`,{
            method: 'DELETE',
            headers:{
              'Content-Type': 'application/json'
            }
          })
          .then(res => res.json())
          .then(()=>handelRemoveBook(book))

    }

    const likeStyles = {
        color: "red"
    }
    const dislikeStyles = {
        color: "black"
    }

    return(
        <div className="card">

            <h2>{title}</h2>
            <img
            src={image}
            alt={title}
            className="book-avatar"
            />
            {like? <h4 style={likeStyles} onClick={handleLike}>Like ♥</h4>:<h4 style={dislikeStyles} onClick={handleLike}>Like ♡</h4>}
            <p>Current Chapter {chapters}</p>

            <Link to={`/Books/${id}`}>More Details</Link>
            <Link to={`/EditBook/${id}`}>Edit</Link>
            <button onClick={handleRemove}>Remove</button>

        </div>
    )
}
