import React, {useState,useEffect} from "react";
import { useHistory, useParams } from "react-router-dom";

export default function EditBook({updateBook,books}) {
    const params = useParams()
    const history = useHistory()
    const [bookObj, setBookObj]= useState(books.find((book)=>book.id == params.id))


    function handleChange(e) {
        setBookObj({
          ...bookObj,
          [e.target.name]: e.target.value,
        })
      }

    function handelSubmit(e){
        e.preventDefault()
        const newBook ={
            id: bookObj.id,
            title: bookObj.title,
            image: bookObj.image,
            liked: bookObj.liked == 'true'? true : false,
            chapters: bookObj.chapters,
            summary: bookObj.summary
        }
        fetch(`http://localhost:3000/books/${bookObj.id}`, {
            method: "PATCH",
            headers: {
              "Content-Type": "application/json",
              "Accept": "application/json",
            },
            body: JSON.stringify(newBook),
          })
            .then((r) => r.json())
            .then(()=>{
                updateBook(newBook)
                history.push("/Books")
            })
    }



    return (
        <div className="add-book-form">
            <h1>Edit Books</h1>
            <form onSubmit={handelSubmit}>
            <p>Title </p>
                <input
                type="text"
                name="title"
                label="title"
                value={bookObj.title}
                onChange={handleChange}/>
            <p>Image </p>
                <input
                type="text"
                name="image"
                label="image"
                value={bookObj.image}
                onChange={handleChange}/>
            <p>Favorite</p>
                <input
                type="radio"
                name="liked"
                label="like"
                value={true}
                onChange={handleChange}
                checked={bookObj.liked === true||bookObj.liked === "true"}
                />
            <label>Yes</label>
            <input
                type="radio"
                name="liked"
                label="dislike"
                value={false}
                onChange={handleChange}
                checked={bookObj.liked === false||bookObj.liked === "false"}
                />
            <label>No</label>
            <p>Chapters</p>
            <input
                type="number"
                name="chapters"
                lable="chapter"
                value={bookObj.chapters}
                onChange={handleChange}/>
            <p>Summary</p>
            <textarea
                name="summary"
                label="summary"
                value={bookObj.summary}
                onChange={handleChange}
            />
            <br/>
            <button type="submit">submit</button>

            </form>
        </div>
    )
}
