import React, {useState} from "react";

import { Link } from "react-router-dom";

export default function BookCard({book,handelRemoveBook}){
    const {id, title, image, author, summary} = book


    function handleRemove(){
        fetch(`http://localhost:3000/books/${id}`,{
            method: 'DELETE',
            headers:{
              'Content-Type': 'application/json'
            }
          })
          .then(res => res.json())
          .then(()=>handelRemoveBook(book))

    }


    return(
        <div className="card">

            <h2>{title}</h2>
            <img
            src={image}
            alt={title}
            className="book-avatar"
            />
            {like? <h4 style={likeStyles} onClick={handleLike}>Like ♥</h4>:<h4 style={dislikeStyles} onClick={handleLike}>Like ♡</h4>}
            <p>Current Chapter {chapters}</p>

            <Link to={`/Books/${id}`}>More Details</Link>
            <Link to={`/EditBook/${id}`}>Edit</Link>
            <button onClick={handleRemove}>Remove</button>

        </div>
    )
}
